import React, { Component } from "react";

class Password extends Component {
  state = {
    mode: 0,
    value: ""
  };
  render() {
    switch (this.state.mode) {
      case 0:
        return this.roomEnter();
      case 1:
        return <h1>Room created</h1>;
      default:
        return;
    }
  }

  submitFormHandler() {
    //event.preventDefault();
    //this.setState({ mode: 1, value: this.refs.name.value });
  }

  roomEnter = () => (
    <div>
      <h1>Hi, welcome to Password!</h1>
      <form onSubmit={this.submitFormHandler}>
        <div>
          <input
            type="text"
            name="name"
            ref={input=>this.state.mode = 1}
          />
          <button onClick={this.submitFormHandler}>Create Room</button>
        </div>
      </form>
      <form onSubmit={this.submitFormHandler}>
        <div>
          <input type="text" name="name" ref="name" />
          <button type="submit">Join Room</button>
        </div>
      </form>
    </div>
  );
}

export default Password;
